styles.css:
.title {
  color: transparent;
  -webkit-text-stroke: 3px #ffff;
}
.parallax {
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  /* Set a specific height */
  min-height: 500px;
}
.hero_bg {
  background-image: url("/wp-content/themes/eclipse-creative-portfolio/assets/images/hero_image.png");
  background-position: right;
  background-size: cover;
  background-repeat: no-repeat;
  /* Set a specific height */
  min-height: 500px;
}
.creative-portfolio-hero-background-box {
  width: 30%;
  height: 160px;
  background: #3e3e3e;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 1;
}
.wp-block-media-text.is-stacked-on-mobile .wp-block-media-text__content {
  grid-column: 1;
  grid-row: 2;
}
.wp-block-media-text.is-stacked-on-mobile {
  grid-template-columns: 100% !important;
}
.wp-block-media-text__media {
  padding: 28px;
}
.wp-block-separator {
  background: unset !important;
  color: #ffffff82 !important;
}
marquee {
  font-family: var(--wp--preset--font-family--sans-serif) !important;
  font-weight: 500;
  font-size: 5rem;
  padding: 12px 0;
  text-transform: uppercase;
}
.marquee1 {
  color: var(--wp--preset--color--fifth) !important;
}
.marquee2 {
  color: transparent;
  -webkit-text-stroke: 1px var(--wp--preset--color--fifth) !important;
}
.justify {
  text-align: justify;
  text-justify: inter-word;
}
.wp-block-media-text__content p {
  opacity: 0;
  transition: 0.3s ease-in-out;
}
.wp-block-media-text:hover p {
  opacity: 1 !important;
}
.social_primary {
  position: fixed;
  left: 2vw;
  top: 35vh;
}

.our_works_home .wp-block-latest-posts.wp-block-latest-posts__list li {
  padding-bottom: 48px;
  position: relative;
}
.our_works_home .wp-block-latest-posts.wp-block-latest-posts__list li a {
  padding-top: 32px;
  padding-bottom: 120px;
  width: 300px;
  word-wrap: break-word;
  display: flex;
}

.our_works_home
  .wp-block-latest-posts.wp-block-latest-posts__list
  li:nth-child(even) {
  left: 30%;
}

.our_works_home
  .wp-block-latest-posts.wp-block-latest-posts__list
  li:nth-child(odd) {
  right: 30%;
}

.our_works_home
  .wp-block-latest-posts.wp-block-latest-posts__list
  li:nth-child(even)
  a {
  float: right;
  text-align: right;
}

.our_works_home
  .wp-block-latest-posts.wp-block-latest-posts__list
  li:nth-child(odd)
  a {
  float: left;
  text-align: left;
}

.our_works_home .wp-block-latest-posts__featured-image img {
  width: 500px;
  height: 500px;
  object-fit: cover;
  transition: 0.5s ease-in-out;
}
.our_works_home .wp-block-latest-posts__featured-image img:hover {
  border-radius: 50% !important;
}
.table-services table td {
  padding: 120px 60px;
  transition: 0.3s ease-in-out;
}
.table-services table td:hover {
  color: var(--wp--preset--color--first);
  background-color: var(--wp--preset--color--white) !important;
}

.table-services table tr:nth-child(even) td:nth-child(odd),
.table-services table tr:nth-child(odd) td:nth-child(even) {
  background-color: var(--wp--preset--color--second);
}
.faq_ul {
  list-style: none;
}
.faq_ul li {
  padding: 32px 18px;
  transition: 0.3s ease-in-out;
}
.faq_ul li:hover {
  color: var(--wp--preset--color--first);
  background-color: var(--wp--preset--color--white) !important;
}
.faq_ul li:nth-child(odd) {
  background-color: var(--wp--preset--color--first);
}

.news .wp-block-latest-posts.wp-block-latest-posts__list li {
  padding-bottom: 48px;
  position: relative;
}
.news .wp-block-latest-posts.wp-block-latest-posts__list li a {
  width: 600px;
  word-wrap: break-word;
  display: flex;
  text-decoration: none;
  padding-top: 32px;
}
.news .wp-block-latest-posts.wp-block-latest-posts__list li:nth-child(even) {
  left: 70%;
}

.news .wp-block-latest-posts.wp-block-latest-posts__list li:nth-child(odd) {
  right: 70%;
}
.news .wp-block-latest-posts__post-author {
  padding-top: 32px;
  color: var(--wp--preset--color--fifth) !important;
  font-size: var(--wp--preset--font-size--medium);
  font-weight: 500;
}
.news .wp-block-latest-posts__post-date {
  color: var(--wp--preset--color--fortext) !important;
  font-size: var(--wp--preset--font-size--medium);
}
.list_portfolio p a:hover {
  transition: 0.5s ease-in-out;
}
.list_portfolio p a:hover {
  color: var(--wp--preset--color--fifth) !important;
}

/* CSS para dispositivos móveis (incluindo tablets) */
@media (max-width: 768px) {
  /* Adicione as animações CSS aqui */
  .our_works_home .wp-block-latest-posts__featured-image img {
    width: 380px !important;
    height: 380px !important;
  }
  .our_works_home .wp-block-latest-posts.wp-block-latest-posts__list li {
    padding-bottom: 48px;
    position: static;
  }
  .title,
  footer h2,
  .sample_page h2,
  .text_404 {
    font-size: 3.2rem !important;
  }
  .text_404 {
    padding-top: 32px;
  }
  .social_primary {
    display: none;
  }
  .about_section .wp-block-heading {
    padding-top: 26px;
  }
  .parallax p {
    font-size: 28px !important;
  }
  header .wp-block-group,
  .group_development .wp-block-group,
  .about_section,
  .about_section .wp-block-group,
  .team_section,
  .team_section .wp-block-group,
  footer .wp-block-group,
  .title,
  .hero_bg,
  .parallax,
  .parallax p,
  .services,
  .faq_ul,
  .faq_section .wp-block-group,
  .review_section,
  .review_section .wp-block-group,
  .mobile_404,
  .mobile_404 .wp-block-group,
  .content_mobile_header,
  .content_mobile .wp-block-group,
  .sample_page, 
  .sample_page .wp-block-group {
    padding-left: 16px !important;
    padding-right: 16px !important;
  }
  .services td {
    padding: 24px !important;
    font-size: 14px !important;
  }
  .faq_ul li {
    font-size: 18px !important;
  }
  header .is-layout-flex {
    flex-wrap: initial !important;
  }
  .h1_intro {
    padding: 120px 16px 16px 16px !important;
  }
  .news .wp-block-latest-posts.wp-block-latest-posts__list li {
    position: initial;
    font-size: 32px !important;
  }
  .news .wp-block-latest-posts.wp-block-latest-posts__list li a {
    width: 100%;
    display: contents;
  }
  .news_spacer_mobile .wp-block-spacer {
    height: 60px !important;
  }
  .sub_menu_mobile {
    display: none;
  }
}

.navigation_teste {
  display: none !important;
}
/*make the menu sub-menu items drop down on mouse hover */
ul.sub-menu {
  display: none;
}

ul.menu li.menu-item-has-children:hover > ul.sub-menu {
  display: block;
}
